import React, { useState } from 'react';
import cityData from '../city_coordinates.json'

const CityDropdown = ({ onCityChange }) => {
  const [selectedOption, setSelectedOption] = useState('');

  const options = cityData;

  const handleSelectChange = (event) => {
    console.log(event.target.value.json())

    setSelectedOption(event.target.value);

    onCityChange(event.target.value);
  };

  return (
    <div>
      <label htmlFor="dropdown">Choose a city: </label>

      <select
        id="dropdown"
        value={selectedOption}
        onChange={handleSelectChange}
      >
        {options.map((option) => (
          <option key={option.city} value={option}>
            {option.city}
          </option>
        ))}
      </select>

      <div>
        <strong>Selected Option: </strong>
        {selectedOption ? selectedOption : 'None'}
      </div>
    </div>
  );
};

export default CityDropdown;
