import logo from './logo.svg';
import './App.css';

function App() {

  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  const latitude = 51.5074;
  const longitude = -0.1278;

  const url = `http://www.7timer.info/bin/api.pl?lon=${longitude}&lat=${latitude}&product=civillight&output=json`;

  // Fetch weather data when the component mounts
  useEffect(() => {
    console.log("IN THE USEEFFECT")
      const fetchWeatherData = async () => {
          try {
              setLoading(true);
              const response = await fetch(url);
              const data = await response.json();
              if (data && data.dataseries) {
              setWeatherData(data.dataseries);
              } else {
              setError('No weather data found');
              }
          } catch (err) {
              setError('Error fetching weather data');
              console.error('Error fetching weather data:', err);
          } finally {
              setLoading(false);
          }
      };

      fetchWeatherData();
  }, []);

  return (
    <div>
      <h1>Weather Data</h1>

      {loading && <p>Loading weather data...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div id="weather-container">
        {weatherData.length > 0 ? (
          weatherData.map((entry, index) => (
            <div key={index} className="weather-item">
              <strong>Weather:</strong> {entry.weather}
            </div>
          ))
        ) : (
          <p>No weather data available.</p>
        )}
      </div>
    </div>
  );
}

export default App;
